import React from 'react';
import Slider from 'react-slick';
import './LogoSlider.css'; // Файл стилів

const logos = [
  { id: 1, src: '../Assets/gucci.png', alt: 'Apple' },
  { id: 2, src: '../Assets/calvin-klein.png', alt: 'Google' },
  { id: 3, src: '../Assets/zara.png', alt: 'Amazon' },
  { id: 4, src: '../Assets/versace.png', alt: 'Microsoft' },
  { id: 5, src: '../Assets/lacoste.png', alt: 'Facebook' },
];

const LogoSlider = () => {
  const settings = {
    infinite: true,
    speed: 3000, // Швидкість прокрутки (у мс)
    slidesToShow: 5, // Кількість видимих слайдів
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 0, // Неперервний рух
    cssEase: 'linear', // Лінійна прокрутка
    arrows: false, // Без стрілок
    pauseOnHover: false, // Прокрутка не зупиняється при наведенні
  };

  return (
    <div className="logo-slider">
      <Slider {...settings}>
        {logos.map((logo) => (
          <div key={logo.id} className="logo-slide">
            <img src={logo.src} alt={logo.alt} />
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default LogoSlider;
