import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./Testimonials.css"; // CSS для стилів

const testimonials = [
  {
    id: 1,
    name: "Anna Smith",
    review:
      "Amazing clothing store! Great quality and the styles are so trendy. Definitely my favorite place to shop.",
    instagram: "https://www.instagram.com", // Посилання на Instagram
  },
  {
    id: 2,
    name: "John Doe",
    review:
      "Fast delivery and excellent service. The clothes fit perfectly and look just like in the photos!",
    instagram: "https://www.instagram.com",
  },
  {
    id: 3,
    name: "Maria Garcia",
    review:
      "I love the variety of options and the affordable prices. Will definitely recommend to my friends!",
    instagram: "https://www.instagram.com",
  },
];

function Testimonials() {
  const settings = {
    dots: true, // Показувати точки навігації
    infinite: true, // Бескінечний слайдер
    slidesToShow: 1, // Показувати один відгук
    slidesToScroll: 1, // Прокручувати по одному відгуку
    autoplay: false, // Відключити автопрокрутку
    arrows: true, // Увімкнути кнопки навігації
  };

  return (
    <div className="testimonials-container">
      <h2 className="section-title">What Our Customers Say</h2>
      <Slider {...settings}>
        {testimonials.map((testimonial) => (
          <div key={testimonial.id} className="testimonial">
            <div className="testimonial-header">
              <h3 className="testimonial-name">{testimonial.name}</h3>
              <a
                href={testimonial.instagram}
                target="_blank"
                rel="noopener noreferrer"
              >
                <img
                  src="https://upload.wikimedia.org/wikipedia/commons/a/a5/Instagram_icon.png"
                  alt="Instagram"
                  className="instagram-logo"
                />
              </a>
            </div>
            <p className="testimonial-review">{testimonial.review}</p>
          </div>
        ))}
      </Slider>
    </div>
  );
}

export default Testimonials;
